mapper.select:
SELECT * FROM (
	SELECT rownum rnum, X.* FROM (


        ORDER BY report_no DESC
	    ) X
	) zask_report
WHERE rnum BETWEEN #{startNo } AND #{endNo }

mapper.update:
UPDATE zask_report
SET report_status = 'y'
WHERE ask_no = #{askNo }

UPDATE zask_report
SET report_status = 'n'
WHERE ask_no = #{askNo }
