mapper.select:
SELECT * FROM (
	SELECT rownum rnum, Q.* FROM (
		SELECT * FROM zqna
		ORDER BY qna_no DESC
	) Q
) zqna
WHERE rnum BETWEEN #{startNo} AND #{endNo}

mapper.update:
UPDATE zqna
SET qna_status = 'y'
WHERE qna_no = #{qnaNo}

UPDATE zqna_comment
SET admin_no = #{adminNo}, qna_com_date = sysdate, qna_com_content = #{qnaComContent}
WHERE qna_com_no = #{qnaComNo}

UPDATE zqna
SET qna_status = 'n'
WHERE qna_no = #{qnaNo}
