mapper.select:
SELECT * FROM (
	SELECT rownum rnum, X.* FROM (


        ORDER BY report_no DESC
    ) X
) zreview_report
WHERE rnum BETWEEN #{startNo } AND #{endNo }

mapper.update:
UPDATE zreview_report
SET report_status = 'y'
WHERE review_no = #{reviewNo }

UPDATE zreview_report
SET report_status = 'n'
WHERE review_no = #{reviewNo }
