mapper.select:
SELECT * FROM (
	SELECT rownum rnum, R.* FROM (


        ORDER BY report_no DESC
    ) R
) ZREVIEW
WHERE rnum BETWEEN #{startNo } AND #{endNo }

mapper.update:
UPDATE zreview_report
SET report_status = 'y'
WHERE review_no = #{reviewNo }

UPDATE zreview_report
SET report_status = 'n'
WHERE review_no = #{reviewNo }

UPDATE ZREVIEW
SET review_hit = review_hit + 1
WHERE review_no = #{reviewNo }
