mapper.select:
SELECT * FROM (
	SELECT rownum rnum, X.* FROM (


        ORDER BY report_no DESC
    ) X
) ztrade_report
WHERE rnum BETWEEN #{startNo } AND #{endNo }

mapper.update:
UPDATE ztrade_report
SET report_status = 'y'
WHERE trade_no = #{tradeNo }

UPDATE ztrade_report
SET report_status = 'n'
WHERE trade_no = #{tradeNo }
