mapper.select:
SELECT COUNT(*) FROM zvote
WHERE sysdate  <  #{voteEnd}
AND ask_no = #{askNo} AND user_no = #{userNo}

mapper.update:
UPDATE zask 
SET ask_hit = ask_hit+1
WHERE ask_no = #{askNo}

UPDATE zask
SET ask_title = #{askTitle }
	, ask_content = #{askContent }
WHERE ask_no = #{askNo }

mapper.select:
SELECT count(*) FROM zask_scrap
WHERE ask_no = #{askNo}
AND user_no = #{userNo}

	    SELECT user_nick
        FROM zuser
        WHERE user_no = #{userNo }

mapper.update:
UPDATE zask_comment
SET ask_com_content = #{askComContent }, ask_com_date = SYSDATE
WHERE ask_com_no = #{askComNo }

mapper.select:
SELECT grade_url
FROM USERWITHURL
WHERE user_no = #{userNo }

SELECT * FROM ( SELECT rownum rnum, A.* FROM ( SELECT DISTINCT A.ask_no, U.user_no, user_nick, F.file_no, F.file_stored , I.item_no, I.item_brand, I.item_name, I.item_price , A.ask_title, A.ask_content, A.ask_date, A.ask_hit, W.grade_url, nvl(P.report_status, 'n') AS report_status , cnt.ask_com_cnt FROM zask A LEFT OUTER JOIN zuser U ON U.user_no = A.user_no LEFT OUTER JOIN zask_comment C on C.ask_no = A.ask_no LEFT OUTER JOIN zitem I ON A.product_no = I.item_no LEFT OUTER JOIN zfile F ON I.file_no = F.file_no LEFT OUTER JOIN USERWITHURL W ON U.user_no = W.user_no LEFT OUTER JOIN zask_report P ON A.ask_no = P.ask_no LEFT OUTER JOIN ( SELECT count (ask_com_no) ask_com_cnt, ask_no FROM ZASK_COMMENT GROUP BY ask_no ) cnt ON cnt.ask_no = A.ask_no
ORDER BY ask_hit DESC
ORDER BY ask_no DESC
ORDER BY ask_no DESC
ORDER BY ask_no DESC
) A ) ZASK WHERE rnum BETWEEN #{startNo } AND #{endNo }
