mapper.select:
SELECT COUNT(*) FROM zvote
WHERE TO_CHAR(SYSDATE, 'yy/MM/dd') < TO_CHAR(vote_end, 'yy/MM/dd')
AND ask_no = #{askNo }

mapper.update:
UPDATE zvote
SET vote_end = sysdate
WHERE ask_no = #{askNo }

mapper.select:
SELECT product_no
FROM zask
WHERE ask_no = #{askNo }

mapper.update:
UPDATE zitem
SET item_status = 'y'
WHERE item_no = #{itemNo }
