mapper.select:
SELECT count (*) FROM zmessage
WHERE receiver_no = #{userNo }
AND receiver_show = 'y'

SELECT count (*) FROM zmessage
WHERE sender_no = #{userNo }
AND sender_show = 'y'

mapper.update:
UPDATE zmessage
SET msg_check = 'y'
WHERE msg_no = #{msgNo }

mapper.select:
SELECT msg_no, receiver_no, sender_no, msg_title, msg_content, msg_date, msg_check
FROM zmessage
WHERE msg_no = #{msgNo }

mapper.update:
UPDATE zmessage
SET receiver_show = 'n'
WHERE msg_no = #{msgNo }

UPDATE zmessage
SET sender_show = 'n'
WHERE msg_no = #{msgNo }
