mapper.select:
SELECT receiver_no
FROM zmessage
WHERE msg_no = #{msgNo }

SELECT sender_no
FROM zmessage
WHERE msg_no = #{msgNo }

mapper.update:
UPDATE zmessage
SET msg_check = 'y'
WHERE msg_no = #{msgNo }

mapper.select:
SELECT msg_no, receiver_no, sender_no, msg_title, msg_content, msg_date, msg_check
FROM zmessage
WHERE msg_no = #{msgNo }

mapper.update:
UPDATE zmessage
SET receiver_show = 'n'
WHERE msg_no = #{msgNo }

UPDATE zmessage
SET sender_show = 'n'
WHERE msg_no = #{msgNo }
