	) V
) R
WHERE rnum between #{paging.startNo } AND #{paging.endNo }

SELECT * FROM (
	SELECT rownum rnum, T.* FROM (
		SELECT * FROM ztrade
		WHERE user_no = #{userNo }
		ORDER BY trade_date DESC
	) T
) R
WHERE rnum between #{paging.startNo } AND #{paging.endNo }

SELECT * FROM (
