mapper.select:
SELECT * FROM ( SELECT rownum rnum, R.* FROM ( SELECT DISTINCT R.review_no, U.user_no, U.user_nick, R.file_no, F.file_stored , R.review_title, R.review_date, R.review_hit, W.grade_url, nvl(P.report_status, 'n') AS report_status , cnt.review_com_cnt FROM zreview R LEFT OUTER JOIN ZUSER U ON U.user_no(+) = R.user_no LEFT OUTER JOIN USERWITHURL W ON U.user_no = W.user_no LEFT OUTER JOIN zreview_report P ON R.review_no = P.review_no LEFT OUTER JOIN zfile F ON F.file_no = R.file_no LEFT OUTER JOIN ( SELECT count (review_com_no) review_com_cnt, review_no FROM ZREVIEW_COMMENT GROUP BY review_no ) cnt ON cnt.review_no = R.review_no
ORDER BY review_hit DESC
ORDER BY review_no DESC
ORDER BY file_no DESC
ORDER BY review_no DESC
) R ) ZREVIEW WHERE rnum BETWEEN #{startNo } AND #{endNo }

mapper.update:
UPDATE ZREVIEW
SET review_hit = review_hit + 1
WHERE review_no = #{reviewNo }

UPDATE ZREVIEW
SET file_no = #{fileNo}
	,review_title = #{reviewTitle }
	, review_content = #{reviewContent }
WHERE review_no = #{reviewNo }

mapper.delete:
DELETE zreview_scrap
WHERE user_no = #{userNo } 
	AND review_no = #{reviewNo }

mapper.select:
SELECT count(*) FROM zreview_scrap
WHERE review_no = #{reviewNo } 
	AND user_no = #{userNo }

 	SELECT count(*) FROM zreview_report
	WHERE review_no = #{reviewNo }
		AND reporter_no = #{reporterNo }
