mapper.select:
SELECT * FROM (
    SELECT rownum rnum, R.* FROM (


        ORDER BY review_no DESC
    ) R
) ZREVIEW
WHERE rnum BETWEEN #{startNo } AND #{endNo }

mapper.update:
UPDATE ZREVIEW
SET review_hit = review_hit + 1
WHERE review_no = #{reviewNo }

UPDATE ZREVIEW
SET review_title = #{reviewTitle }
	, review_content = #{reviewContent }
WHERE review_no = #{reviewNo }
